# -*- coding: utf-8 -*-
import xlrd
import sys
class Gerador():
    planilha = '/home/henrique/Downloads/parametros.xls'
    rodape = '<p class="Tabela_Texto_Centralizado">Defensoria P&uacute;blica do Estado de Santa Catarina</p>\n<p class="Tabela_Texto_Centralizado">{{atendimento.defensoria.predio.endereco.logradouro}},{{atendimento.defensoria.predio.endereco.numero}},{{atendimento.defensoria.predio.endereco.bairro}} {{atendimento.defensoria.predio.endereco.cidade}}, CEP {{atendimento.defensoria.predio.endereco.cep}} Fone {{atendimento.defensoria.predio.telefone}}</p>'
    cabecalho = '<p class="Tabela_Texto_8"><img alt="" src="data:image/png;base64," style="height:86px; margin-bottom:0px; margin-left:0px; margin-right:0px; margin-top:0px; width:302px" /></p>'
    conteudo = ''
    versao_numero =1
    eh_modelo = True
    esta_assinado = False
    esta_pronto_para_assinar = False
    criado_por_nome= 'admin'
    criado_em = 'now()'
    esta_ativo = True
    criado_por_id =1
    eh_modelo_padrao =False
    page_margin_bottom =35.5
    page_margin_left =1
    page_margin_right=4
    page_margin_top=41.5
    modelo_pronto_para_utilizacao= True
    modelo_publico= True
    uuid='(SELECT md5(random()::text || clock_timestamp()::text)::uuid)'
    is_removed = False
    def __init__(self):
        reload(sys)
        sys.setdefaultencoding('utf8')
        workbook = xlrd.open_workbook(self.planilha)
        worksheet = workbook.sheet_by_index(0)
        parametros = []
        keys = [v.value for v in worksheet.row(0)]
        for row_number in range(worksheet.nrows):
            if row_number == 0:
                continue
            row_data = {}
            for col_number, cell in enumerate(worksheet.row(row_number)):
                row_data[keys[col_number]] = cell.value
            parametros.append(row_data)
        quantidade_parametros = len(parametros)
        print(str(quantidade_parametros) + " Imoveis para processamento")

        outfile = open("/home/henrique/Downloads/output.txt", "w")
        for parametro in parametros:
            tipo=parametro['tipo']
            assunto = parametro['assunto']
            queryTipo = '(SELECT id FROM public.djdocuments_tipodocumento where titulo = \'{}\' limit 1)'.format(tipo)
            query ='INSERT INTO public.djdocuments_documento(versao_numero, pk_uuid, assunto, cabecalho,conteudo, rodape, eh_modelo, modelo_descricao, ' \
                   'esta_assinado, esta_pronto_para_assinar, criado_por_nome, criado_em, esta_ativo, criado_por_id, tipo_documento_id, eh_modelo_padrao,' \
                   ' page_margin_bottom, page_margin_left, page_margin_right, page_margin_top, modelo_pronto_para_utilizacao, modelo_publico,modificado_por_nome,' \
                   'excluido_por_nome,is_removed,conteudo_assinaturas,rodape_qr_validacao,finalizado_por_nome)VALUES ' \
                   '({}, {}, \'{}\',\'{}\',\'{}\',\'{}\', {}, \'{}\', ' \
                   '{}, {}, \'{}\', {}, {}, {}, {}, {}, {}, {}, {}, {}, {}, {},\'{}\',\'{}\',{},\'{}\',\'{}\',\'{}\');\n\n'\
                .format(self.versao_numero,self.uuid,assunto,self.cabecalho,self.conteudo,self.rodape,self.eh_modelo,assunto,self.esta_assinado,self.esta_pronto_para_assinar,
                        self.criado_por_nome,self.criado_em,self.esta_ativo,self.criado_por_id,queryTipo,self.eh_modelo_padrao,self.page_margin_bottom,self.page_margin_left,
                        self.page_margin_right,self.page_margin_top,self.modelo_pronto_para_utilizacao,self.modelo_publico,'','',self.is_removed,'','','')
            print(query)
            outfile.writelines(query)
        outfile.close()

gerar = Gerador()


